require("dotenv").config();
const Discord = require("discord.js");
const client = new Discord.Client();

const prefix = "!";

client.once("ready", () => {
	console.log("Sir Wellington is at your service ;)");
});
let raceStarted = false;
let race_text = null;
client.on("message", (message) => {
	if (raceStarted && !message.author.bot) {
		if (message.content === race_text) {
			message.reply("You win!");
			race_text = null;
			raceStarted = false;
		} else {
			message.reply(`Not right!`);
		}
	}

	if (!message.content.startsWith(prefix) || message.author.bot) return;

	const args = message.content.slice(prefix.length).split(/ +/);
	const command = args.shift().toLowerCase();

	if (command === "race") {
		// message.channel.send("Here's your image", {
		// 	files: ["./assets/race_images/carbon.png"],
		// });
		race_text = mainRace(message);
		raceStarted = true;
	}
});

client.login(process.env.TOKEN);
